-- Create Pokemon table
CREATE TABLE Pokemon (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255),
    base_experience INT,
    height FLOAT,
    order_num INT,
    species_name VARCHAR(255),
    sprites JSONB,
    weight FLOAT,
    abilities JSONB,
    forms JSONB,
    game_indices JSONB,
    held_items JSONB,
    is_default BOOLEAN,
    location_area_encounters VARCHAR(255),
    moves JSONB,
    past_types JSONB,
    stats JSONB,
    types JSONB
);
